//for question
document.addEventListener("DOMContentLoaded", function(){
    var qSummernote = $('#qSummernote');
   qSummernote.on('summernote.keyup', function () {
        var updatedContent = qSummernote.summernote('code');
        document.getElementById('previewQTitleDiv').innerHTML = `
            ${updatedContent}
        `;
    });
    qSummernote.css({
        'display': `${dispType}`
    });

});

//for sidebar
document.addEventListener("DOMContentLoaded", function(){
    var sidebarSummernote= $("#sBDivSummernote");
    var hintSummernote = $("#hintDivSummernote");
    sidebarSummernote.on('summernote.keyup', function(){
    var updatedContent = sidebarSummernote.summernote('code');
    document.getElementById('sidebarCnt').innerHTML = `${updatedContent}`;
    });
    sidebarSummernote.css({
        'display': `${dispType}`
    });
    hintSummernote.on('summernote.keyup', function(){
        var updatedHint = hintSummernote.summernote('code');
        document.getElementById('hintCnt').innerHTML = `${updatedHint}`;
    });
    hintSummernote.css({
        'display': `${dispType}`
    });
});

function showDIv(divRef){
    var div_ = document.getElementById(`#${divRef}`);
    div_.classList.toggle("visibleDiv");
}
