document.addEventListener("DOMContentLoaded", function(){
    var qSummernote = $('#qSummernote');
   qSummernote.on('summernote.keyup', function () {
        var updatedContent = qSummernote.summernote('code');
        document.getElementById('previewQTitleDiv').innerHTML = `
            ${updatedContent}
        `;
    });
    qSummernote.css({
        'display': `${dispType}`
    });

});


function showDIv(divRef){
    var div_ = document.getElementById(`#${divRef}`);
    div_.classList.toggle("visibleDiv");
}
