//function to update the preview based on formatting options

//function to update the preview based on formatting options
  // Function to update the preview based on formatting options

  function updatePreview() {
    const questionDisplay = document.querySelector('select[name="question_display"]').value;
    const answerDisplay = document.querySelector('select[name="answer_display"]').value;
    const questionWrapping = document.querySelector('select[name="question_wrapping"]').value;
    const answerWrapping = document.querySelector('select[name="answer_wrapping"]').value;
    const justifyContent = document.querySelector('select[name="justify_content"]').value;
    const alignItems = document.querySelector('select[name="align_items"]').value;
    const margin = document.querySelector('select[name="margin"]').value;

    // Replace this part with your actual question and answer content
    const sampleQuestion = 'Sample Question';
    const sampleAnswers = ['Answer 1', 'Answer 2', 'Answer 3'];

    // Update the question preview
    const questionElement = document.getElementById('question');
    questionElement.innerHTML = `<div style="display: ${questionDisplay}; flex-wrap: ${questionWrapping}; justify-content: ${justifyContent}; align-items: ${alignItems}; margin: ${margin}px">${sampleQuestion}</div>`;

    // Update the answers preview
    const answersElement = document.getElementById('answers');
    answersElement.innerHTML = `<div style="display: ${answerDisplay}; flex-wrap: ${answerWrapping}; justify-content: ${justifyContent}; align-items: ${alignItems}; margin: ${margin}px">${sampleAnswers.join('<br>')}</div>`;
  }

  // Attach the updatePreview function to the change event of each formatting option
  const formattingInputs = document.querySelectorAll('#dispDiv select');
  formattingInputs.forEach(input => {
    input.addEventListener('change', updatePreview);
  });

  // Initial preview update
  updatePreview();



function showDIv(divRef){
    var div_ = document.getElementById(`#${divRef}`);
    div_.classList.toggle("visibleDiv");
}
